# Special
background='#161d24'
foreground='#c4c6c8'
cursor='#c4c6c8'

# Colors
color0='#161d24'
color1='#3571C9'
color2='#6D8FA5'
color3='#509FCE'
color4='#A0A6AC'
color5='#E2C1A4'
